//       <Hello name='Jeryll' />
//       <Hello name='Jeremy' />
//       <Hello name='Jamie' />
//     </div>
//   )
// }

// With fragmenting...
const App = () => {
  const name = 'Jeryll'
  const age = 25

  return (
    <>
      <h1>Greetings</h1>
      <Hello name={name} age={age} />
      <Hello name='Jeremy' age={24} />
      <Hello name='Jamie' age={22} />
    </>
  )
}

