const App = () => {
  const name = 'Jeryll'
  const age = 25

  return (
    <div>
      <h1>Greetings</h1>
      <Hello name={name} age={age} />
      <Hello name='Jeremy' age={24} />
      <Hello name='Jamie' age={22} />
    </div>
  )
}

export default App;
